const badgeListener=()=>{const e=document.getElementById("badges-container").children.length;for(let t=1;t<=e;t++){let e=document.getElementById(`badge_${t}`),n=e.lastElementChild;e.addEventListener("mouseenter",()=>{n.classList.remove("visible")}),e.addEventListener("mouseleave",()=>{n.classList.add("visible")})}};badgeListener();